import { Component, Input, OnInit } from '@angular/core';
import { WebBackendService } from 'src/app/services/web-backend.service';

@Component({
  selector: 'app-game-box',
  templateUrl: './game-box.component.html',
  styleUrls: ['./game-box.component.css']
})
export class GameBoxComponent implements OnInit {
  coverIDResult: any;
  @Input() name: string = "";
  @Input() coverID: number = 0;
  @Input() genreIDs: number[] = [];
  @Input() platformsIDs: number[] = [];
  @Input() websitesIDs: number[] = [];

  coverUrl: any = "";
  genreNames: any;
  platFormNames: any;
  constructor(private clientAPI: WebBackendService) {}

  async setupData() {
    let results: any;
     await this.clientAPI.getGameCover(this.coverID).then(data => {
      data.subscribe(value => {
        this.coverUrl = Object.values(value)[0].url;
      })
    })
    await setTimeout(() => {}, 5000)
    // get genre ids
    await this.clientAPI.getGenreNames(this.genreIDs).then(data => {
      data.subscribe(value => {
        this.genreNames = Object.values(value);
      })
    })
    // get the cover ID here
    // get platform ids
    await this.clientAPI.getPlatformNames(this.platformsIDs).then(data => {
      data.subscribe(value => {
        this.platFormNames = Object.values(value);
      })
    })
  }

  ngOnInit(): void {
    this.setupData();
  }

}
<div>
  <h3>name: {{ name }}</h3>
  <div>
    <img [src]="coverUrl" alt="">
  </div>
  <button></button>
</div>
