import { Component } from '@angular/core';
import { SearchService } from './services/search.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  isSlideBarNotActive: boolean = false;
  constructor(private searchService: SearchService) { }

  handleSlidebarStatus(e: any) {
    this.isSlideBarNotActive = e;
  }
  openSlideBar() {
    if (this.isSlideBarNotActive) {
      this.isSlideBarNotActive = false;
      return;
    }
    this.isSlideBarNotActive = true;
    // use boolean for opening and closing the slide bar
    // create another component for slide bar make it fixed or sticky position and absolute
  }
}
<div id="main-outer-divcontainer">
  <app-slidebar (updateSlidebarStatus)="handleSlidebarStatus($event)" *ngIf="isSlideBarNotActive"></app-slidebar>
  <div id="outer-divcontainer">
    <div id="nav-bar">
      <div></div>
      <div class="hide-mobiletablet-view" id="navbar-hamburgerIcon-divcontainer">
        <svg (click)="openSlideBar()" id="navbar-svg" width="30" height="23" viewBox="0 0 30 23" fill="none" xmlns="http://www.w3.org/2000/svg">
          <g id="navBarIcon">
          <rect id="Rectangle 5" y="18.0126" width="29.0526" height="4.06737"/>
          <rect id="Rectangle 6" y="9.29684" width="29.0526" height="3.48632"/>
          <rect id="Rectangle 7" width="29.0526" height="4.06737"/>
          </g>
          </svg>
      </div>

    <router-outlet></router-outlet>
    <app-footer></app-footer>
  </div>
</div>
