import { Component, OnInit, Output, EventEmitter, OnDestroy } from '@angular/core';
import { SearchService } from 'src/app/services/search.service';
import { WebBackendService } from 'src/app/services/web-backend.service';

@Component({
  selector: 'app-search-bar',
  templateUrl: './search-bar.component.html',
  styleUrls: ['./search-bar.component.css']
})
export class SearchBarComponent implements OnInit {
  @Output() sendsearchName = new EventEmitter();
  @Output() sendSearchGenre = new EventEmitter();
  noInput: boolean = false;

  name: string = "";
  errMsg: string = "Please enter a name...";

  // list of genreIDs for searching
  genreSearch: number[] = [];
  constructor(private clientAPI: WebBackendService, private searchService:SearchService) {
    //searchService.setName = [1,4,8,16];
  }
  async setUpGenreIDs() {
    // client
    await this.clientAPI.getAllGenreNames().then(data => {
      let subs = data.subscribe(value => {
        this.searchService.setSearchedGenres = Object.values(value);
        console.log(Object.values(this.searchService.getSearchedGenres));
        if (this.searchService.getSearchedGenres) {
          subs.unsubscribe();
        }
      })
     })
  }
  ngOnInit(): void {
    this.setUpGenreIDs();
  }
  onClick() {
    if (this.name) {
      this.sendsearchName.emit(this.name);
      this.noInput = false;
    }else { 
      this.noInput = true;
    }
    // click send data that should trigger the search game
    console.log("test");
  }
}
