import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { SearchService } from 'src/app/services/search.service';
import { WebBackendService } from 'src/app/services/web-backend.service';

@Component({
  selector: 'app-selection',
  templateUrl: './selection.component.html',
  styleUrls: ['./selection.component.css']
})
export class SelectionComponent implements OnInit {
  @Output() selectedIDS = new EventEmitter;

  isSelectionActive: boolean = false;
  clickedTimes: any;
  selectedids: number[] = [];
  genres: any[];
  constructor(private clientAPI: WebBackendService, private searchService: SearchService) {
    this.genres = [];
    //searchService.setSearchedGenres = [{id: 1, name:"test1"},{id: 2, name:"test2"}]
  }
  ngOnInit(): void {
    this.setUpGenreIDs();
  }
  async setUpGenreIDs() {
    // client
    this.clientAPI.getAllGenreNames().then(data => {
      let subs = data.subscribe(value => {
        this.genres = Object.values(value);
        if (this.genres) {
          subs.unsubscribe();
        }
      })
    })
    //console.log(this.searchService.getSearchedGenres);
  }
  // gets the selected ids and throw it back to search bar
  // each button for selecting ids will have this
  selectID(ids: number){
    //  store selectedIds
    console.log(this.clickedTimes)
    // check if five is already in the clicked times then go on with remove clicked times
    let indexFound = this.clickedTimes.findIndex((value)=> {
      return value === ids
    })
    let index = 0;
    if (indexFound >= 0) {
      this.clickedTimes.push({
        id: ids,
        clickedCount: 1
       })
      index = this.clickedTimes.findIndex((value)=> {
        return value.id === ids;
     })
    }
    if (this.clickedTimes[index].clickedCount === 1) {
      this.clickedTimes[index].clickedCount += 1;
      console.log("clicked times: "+this.clickedTimes);
      this.selectedids.push(ids);
      return
    }
    let deleted = this.selectedids.splice(index);
    console.log("selected removed: "+deleted);
    this.clickedTimes[index] = 1;
  }
  filterSearch() {
    //console.log(this.selectedids)
    this.selectedIDS.emit(this.selectedids);
    this.selectedids = [];
    this.isSelectionActive = false;
  }
  isChecked(id: number) {
    return this.selectedids.filter(value => {
      return  value === id;
    })
  }
  showSelections() {
    if (this.isSelectionActive) {
      // emit data here
      // get selected ids
      // if id is selected
      this.isSelectionActive = false;
      return;
    }
    this.isSelectionActive = true;
  }
  showSelectionsMouseOver() {
    this.isSelectionActive = true;
  }
  hideSelectionsMouseOut() {
    this.isSelectionActive = false;
  }
}
<div id="filter-divcontainer">
  <div id="filtergenre-btns-container">
    <button id="genre-btn" (click)="showSelections()">Genres<svg width="128" height="128" viewBox="0 0 128 128" fill="none" xmlns="http://www.w3.org/2000/svg">
      <g clip-path="url(#clip0_79_12)">
      <path d="M5 33.5L67.5 96.5" stroke="white" stroke-width="10"/>
      <path d="M60 96.5L124 34" stroke="white" stroke-width="10"/>
      </g>
      <defs>
      <clipPath id="clip0_79_12">
      <rect width="128" height="128" fill="white"/>
      </clipPath>
      </defs>
      </svg>
      </button>
    <button id="filter-btn" (click)="filterSearch()">Filter</button>
  </div>
  <div id="genreoptions-container" *ngIf="isSelectionActive">
    <div *ngFor=" let genre of genres; let i = index">
        <label for={{genre.name}}>{{genre.name}}</label>
        <input checked={{isChecked(genre.id)}} (click)="selectID(genre.id)" type="checkbox" name={{genre.name}} id="">
    </div>
  </div>
</div>
