import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { WebBackendService } from 'src/app/services/web-backend.service';

@Component({
  selector: 'app-game-view',
  templateUrl: './game-view.component.html',
  styleUrls: ['./game-view.component.css']
})
export class GameViewComponent implements OnInit {
  id: any;
  genreIDs: number[] = [];
  name: any;
  coverUrl: string = "";
  platformNames: any[] = [];
  description: any;
  websites: any[] = [];

  defaultImage: string = "../../../assets/nodataImage.png";

  genreNames: any[] = [];
  constructor(private route: ActivatedRoute,private clientAPI: WebBackendService){}
  checkCoverUrl(url: string) {
    if (url) {
      return url;
    } else {
      return this.defaultImage;
    }
  }
  async setUpGameViewData() {
    let querySubs = await this.route.queryParamMap.subscribe(value => {
      this.id = value.get("id");
    });
    querySubs.unsubscribe();
        // request more info here
    // get gameinfo
    await this.clientAPI.getsingleGameInfo(this.id)
    .then(async (body) => {
      let bodysubs = await body.subscribe( async (body) => {
        let results = Object.values(body);
        //console.log(results[0]);
        this.genreIDs = results[0].genres;
        this.name = results[0].name;
        this.description = results[0].summary;
        // get genre names
        await this.clientAPI.getGenreNames(this.genreIDs)
        .then( async (data) => {
          let subs = data.subscribe(async (value) => {
            this.genreNames = Object.values(value);
            //console.log(this.genreNames.length);
            if (this.genreNames) {
              subs.unsubscribe();
            }
          })
        })
        //get cover
        await this.clientAPI.getGameCover(this.id)
        .then( async (data) => {
          let subs = await data.subscribe((value) => {
            let cover: any;
            cover = Object.values(value)![0];
            this.coverUrl = cover.url;
            if (cover) {
              subs.unsubscribe();
            }
          })
        })
        // get platforms name
        await this.clientAPI.getPlatformNames(results[0].platforms)
        .then(async (data) => {
          let subs = await data.subscribe( async (value) => {
            this.platformNames = await Object.values(value);
            if(this.platformNames) {
              subs.unsubscribe();
            }
          })
        })
        // get website names
        await this.clientAPI.getsingleGameWebsites(this.id)
        .then(async (data) => {
          let subs = await data.subscribe( async (value) => {
            this.websites = await Object.values(value);
            if (this.websites) {
              subs.unsubscribe();
            }
          })
        })

        if(results) {
          bodysubs.unsubscribe();
        }
      })
    })
  }

  ngOnInit(): void {
    setTimeout( async () => {
      await this.setUpGameViewData();
    },500);
  }
}
<div id="gameview-base-divcontainer">
  <div id="gameview-banner-divcontainer">
    <div id="website-urlcontainer">
      <h3 id="sites-style-desktop-container">Websites</h3>
      <p class="hide-desktopview" *ngFor=" let website of websites; let i = index">
        <span><a href={{website.url}}>{{website.url}}</a></span>
      </p>
    </div>
    <!-- <img [src]="coverUrl" loading="lazy" alt="" srcset=""> -->
  </div>
  <div id="gamesubheader-divcontainer">
    <img src={{checkCoverUrl(coverUrl)}} loading="lazy" alt="" srcset="">
    <div>
      <h2 class="hide-desktopview">{{name}}</h2>
      <h3 class="hide-desktopview">Genres: </h3>
      <p class="hide-desktopview">
        <!-- if no data use the default data -->
        <span *ngFor=" let genrename of genreNames">{{genrename.name ? genrename.name+", "  : "Action, Sci-fi, Racing"}}</span>
      </p>
      <h1 class="hide-mobileview" >{{ name }}</h1>
      <p></p>
    </div>
  </div>
  <div id="content-container">
    <section class="hide-mobileview" id="genre-sectionContainer">
        <h3>Genres:</h3>
        <p>
          <!-- if no data use the default data -->
          <span *ngFor=" let genrename of genreNames">{{genrename.name ? genrename.name+", "  : "Action, Sci-fi, Racing"}}</span>
        </p>
    </section>
    <section id="platform-sectionContainer">
        <h3>Platforms:</h3>
        <p>
            <span *ngFor=" let platformname of platformNames" >{{platformname.name ? platformname.name+", " : "Wii, Windows (PC), Xbox"}}</span>
        </p>
    </section>
    <section id="summary-sectionContainer">
        <h3>Summary: </h3>
        <p>{{ description }}</p>
    </section>
    <section class="hide-mobileview" id="websites-sectionContainer">
        <h3>Websites:</h3>
        <p *ngFor=" let website of websites; let i = index">
            <span><a href={{website.url}}>{{website.url}}</a></span>
        </p>
    </section>
  </div>
</div>
