import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-slidebar',
  templateUrl: './slidebar.component.html',
  styleUrls: ['./slidebar.component.css']
})
export class SlidebarComponent {
  @Output() updateSlidebarStatus = new EventEmitter();
  isActive: boolean = false;
  closeSlidebar() {
    this.updateSlidebarStatus.emit(false);
  }
}
<div id="slidebar-container">
  <div id="btn-container">
    <button (click)="closeSlidebar()" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}" routerLink="/">Home</button>
    <button (click)="closeSlidebar()" routerLinkActive="active"  routerLink="about">About</button>
  </div>
</div>
