import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {

}
<div id="footer-container">
  <div id="links-container">
    <a href="https://www.facebook.com/arhon.serrano.58/"><img src="assets/facebookIcon.png" alt="" srcset=""></a>
    <a href="https://github.com/Hnaro"><img src="assets/githubIcon.png" alt="" srcset=""></a>
    <a href="https://www.instagram.com/hnaro_16/?next=%2F"><img src="assets/instagramIcon.png" alt="" srcset=""></a>
  </div>
  <h4>this webiste is using this API from twitch: </h4>
  <a href="https://www.igdb.com/">IGDB.com</a>
    <h4>&copy; copyright 2023</h4>
</div>
