import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-game-box',
  templateUrl: './game-box.component.html',
  styleUrls: ['./game-box.component.css']
})
export class GameBoxComponent {
  @Input() name: string = "";
  @Input() coverID: number = 0;
  @Input() genreIDs: number[] = [];
  @Input() platformsIDs: number[] = [];
  @Input() websitesIDs: number[] = [];
  constructor() {}
}
<div>
  <div>cover id: {{ coverID }}</div>
  <h3>name: {{ name }}</h3>
  <ul>
    <li>genre IDs: {{ genreIDs }}</li>
    <li>platform IDs: {{ platformsIDs }}</li>
    <li>websiteIDs: {{ websitesIDs }}</li>
  </ul>
</div>
