import { Component, OnInit } from '@angular/core';
import { WebBackendService } from './services/web-backend.service';
import { GameResults } from './model/GameResultsMode';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  gamesSearchResults: any;
  constructor(private clientAPI: WebBackendService) {
    this.gamesSearchResults = [];
  }
  ngOnInit(): void {
    let gameresults: any;
    this.clientAPI.getData().subscribe(results => {
      this.gamesSearchResults = Object.values(results);
      console.log(this.gamesSearchResults);
    });
/*     this.clientAPI.searchName("game")
    .subscribe(value => {
      this.gamesSearchResults = Object.values(value);
      //console.log(this.gamesSearches);
    });
    this.clientAPI.searchGenre(2).subscribe(value => {
      this.genreSearchResults = Object.values(value);
      console.log(this.genreSearchResults);
    }) */
    // get the covers also for each games
  }
}
<h1>Games</h1>
<div *ngFor=" let games of gamesSearchResults; let i = index">
  <p>name: {{ games[i].name }} <br>
    cover ids: {{games[i].cover}} <br>
    genres ids: {{games[i].genres}} <br>
    platforms ids: {{games[i].platforms}} <br>
    websites ids: {{games[i].websites}}
  </p>
</div>
