import { Component, OnInit } from '@angular/core';
import { WebBackendService } from './services/web-backend.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  gamesSearches: any;
  constructor(private clientAPI: WebBackendService) {
    this.gamesSearches = [];
  }
  ngOnInit(): void {
    //this.clientAPI.runTest();
    this.clientAPI.getData()
    .subscribe(value => {
      this.gamesSearches = Object.values(value);
      console.log(this.gamesSearches);
    });
    // get the covers also for each games
  }
}
<h1>Games</h1>
<div *ngFor=" let games of gamesSearches">
  <p>{{ games.name }}, <br> cover ids: {{games.cover}}</p>
</div>
